/**
* Создим класс Pokemon
*/
module.exports = class Pokemon {
    
    constructor(name, lvl) {
        
        this.name = name;
        this.lvl = lvl;
        
        if(typeof this.name == 'object') {
            this.name = name[0].name;
            this.lvl = name[0].lvl;
        }
    }
    
    show() {
        console.log(`покемон: ${this.name}, уровень: ${this.lvl}`)
    }
    
    valueOf() {
        return this.lvl;
    }
}